mapper.select:
SELECT * FROM ${tableName}



			${column_code} LIKE CONCAT('%', #{keyword}, '%')


			${column_name} LIKE CONCAT('%', #{keyword}, '%')


			${column_code} LIKE CONCAT('%', #{keyword}, '%') OR ${column_name} LIKE
			CONCAT('%', #{keyword}, '%')



		AND management_code = #{forCommonType}
